import React, {useState} from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'

import Styling from '../styling/Styling'

import Breakfast from '../components/Meals/Breakfast'
import Lunch from '../components/Meals/Lunch'
import Dinner from '../components/Meals/Dinner'
import { fade, makeStyles } from '@material-ui/core/styles'
import AddCircleIcon from '@material-ui/icons/AddCircle';
// import $ from "../nutritionLabel"
import {InputBase, Toolbar, TextField, Button} from "@material-ui/core"
const Authorization = () => {

  const[date, setDate] =useState(new Date())
  const[finditem, setFinditem] = useState("")

  const onChange = () => {
    setDate(date)
  }

  const handleChange = (event) => {
    setFinditem(event.target.value)
  }

  const handleAdd = () => {
    fetch("http://localhost:3000/finditem", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({item: finditem})
    })
    .then(res => res.json())
    .then(console.log)
  }


  const useStyles = makeStyles((theme) => ({
   inputRoot: {
    color: 'inherit',
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: '50%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: '200px',
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  }
}))

  return (
    <header className="App-page">
       <h1>Your Food Log</h1>
       {/* {$('#test1').nutritionLabel()} */}
        <div className='rowC'>
            <div className="container-1">
              <h1>Calendar</h1>
              <Calendar className="calendar" onChange={onChange} value={date} />
            </div>
            <div className='container-2'>
              <Toolbar>
                <div className={useStyles().search}>
                  <div className={useStyles().searchIcon}>
                    <AddCircleIcon />
                  </div>
                  <InputBase
                    onChange={handleChange}
                    placeholder="   Add.."
                    classes={{
                      root: useStyles().inputRoot,
                      input: useStyles().inputInput,
                    }}
                    inputProps={{ 'aria-label': 'search' }}
                    />
                  </div>
                  <div className={Styling().authBox}>
                    <TextField 
                    label="Quantity" 
                    id="standard-size-small" 
                    size="small" 
                    variant="filled"
                    color="secondary"
                    type="number"
                    />

                  </div>
                  <div className={Styling().authBox}>
                       <label >Unit </label>
                    <select name="cars" id="cars">
                        <option value="null"></option>
                        <option value="oz">oz</option>
                        <option value="grams">grams</option>
                        <option value="cup">cup</option>
                    </select>
                  </div>
                  <div className={Styling().authBox}>
                       <label >For </label>
                    <select name="cars" id="cars">
                        <option value="null"></option>
                        <option value="breakfast">Breakfast</option>
                        <option value="lunch">Lunch</option>
                        <option value="dinner">Dinner</option>
                    </select>
                  </div>
                  <div className={Styling().authBox}>
                    <Button variant="outlined" size="small" onClick={handleAdd}>
                        Add
                    </Button>
                  </div>

                </Toolbar>
                <div className='s-meal'><Breakfast /></div>
                <div className='s-meal'><Lunch /></div>
                <div className='s-meal'><Dinner /></div>
            </div>
        </div>
    </header>

  )
}



export default Authorization;

