import React, {useState, useEffect} from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'

import Styling from '../styling/Styling'

import FoodsDisplay from '../components/Meals/FoodsDisplay'
import Breakfast from '../components/Meals/Breakfast'
import Lunch from '../components/Meals/Lunch'
import Dinner from '../components/Meals/Dinner'

import SearchIcon from '@material-ui/icons/Search';
// import $ from "../nutritionLabel"
import {InputBase, Toolbar, TextField, Button} from "@material-ui/core"
const Authorization = () => {

  const[date, setDate] =useState(new Date())
  const[finditem, setFinditem] = useState("")
  const [foods,setFoods] = useState([])
  const [search, setSearch] = useState("")

  const onChange = () => {
    setDate(date)
  }

  const handleChange = (event) => {
    setFinditem(event.target.value)
  }

  const handleAdd = () => {
    fetch("http://localhost:3000/finditem", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({item: finditem})
    })
    .then(res => res.json())
    .then(console.log)
  }


  useEffect(() => {
    fetch("http://localhost:3000/foods")
    .then(res => res.json())
    .then(setFoods)
  },[])


  const searchFoods = () => foods.filter(food => food.name.toLowerCase().includes(search.toLowerCase()))

  console.log(searchFoods())

  return (
    <header className="App-page">
       <h1>Your Food Log</h1>
       {/* {$('#test1').nutritionLabel()} */}
        <div className='rowC'>
            <div className="container-1">
              <h1>Calendar</h1>
              <Calendar className="calendar" onChange={onChange} value={date} />
            </div>
            <div className='container-2' >
              <Toolbar>
                <div className={Styling().search}>
                  <div className={Styling().searchIcon}>
                    <SearchIcon />
                  </div>
                  <InputBase
                    onChange={handleChange}
                    placeholder="   Search.."
                    classes={{
                      root: Styling().inputRoot,
                      input: Styling().inputInput,
                    }}
                    inputProps={{ 'aria-label': 'search' }}
                    />
                  </div>
                  <div className={Styling().authBox}>
                    <TextField 
                    label="Quantity" 
                    id="standard-size-small" 
                    size="small" 
                    variant="filled"
                    color="secondary"
                    type="number"
                    />

                  </div>
                  <div className={Styling().authBox}>
                       <label >Unit </label>
                    <select name="cars" id="cars">
                        <option value="null"></option>
                        <option value="oz">oz</option>
                        <option value="grams">grams</option>
                        <option value="cup">cup</option>
                    </select>
                  </div>
                  <div className={Styling().authBox}>
                       <label >For </label>
                    <select name="cars" id="cars">
                        <option value="null"></option>
                        <option value="breakfast">Breakfast</option>
                        <option value="lunch">Lunch</option>
                        <option value="dinner">Dinner</option>
                    </select>
                  </div>
                  <div className={Styling().authBox}>
                    <Button variant="outlined" size="small" onClick={handleAdd}>
                        Add
                    </Button>
                  </div>

                </Toolbar>
                  <div className='rowC'>
                    <div>
                      <div className='s-meal'><Breakfast /></div>
                      <div className='s-meal'><Lunch /></div>
                      <div className='s-meal'><Dinner /></div>
                    </div>
                    <div className='display-foods'>
                      <FoodsDisplay foods={searchFoods()}/>
                    </div>
                  </div>
            </div>
        </div>
    </header>

  )
}



export default Authorization;

