import React, {useEffect} from 'react';
import {CardHeader, Card, CardMedia, Typography, Avatar, CardContent} from "@material-ui/core"
import { red } from '@material-ui/core/colors';

import { fade, makeStyles } from '@material-ui/core/styles'

const News = ({news}) => {

  const useStyles = makeStyles((theme) => ({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    },
    avatar: {
      backgroundColor: red[500],
    },
  }));

  return (
    <React.Fragment>
        <div className="sub-container">
        <Card className={useStyles().root}>
        <CardHeader
          avatar={
            <Avatar aria-label="recipe" className={useStyles().avatar}>
              {news.author ? news.author.slice(0,1) : ""}
            </Avatar>
          }
          title= {news.title}
          subheader={news.publishedAt.toString()}
        />
        <CardMedia
          className={useStyles().media}
          image={news.urlToImage}
          title="Paella dish"
        />
        <CardContent>
          <Typography variant="body2" color="textSecondary" component="p">
            {news.description}
          </Typography>
        </CardContent>
        </Card>
      </div>
    </React.Fragment>
  )
}

export default News;
